<?php

namespace ElementsHiveForBreakdance\Admin;

if ( ! defined( 'ABSPATH' ) ) {
	exit;
}


/**
 * Register menu
 */
add_action( 'admin_menu', __NAMESPACE__ . '\add_elements_hive_admin_menu' );

/**
 * Add Plugin admin menu
 *
 * @return void
 */
function add_elements_hive_admin_menu() {
	add_menu_page(
		'Elements Hive',
		'Elements Hive',
		'manage_options',
		'elements_hive',
		'',
		'data:image/svg+xml;base64,'
	);

	add_submenu_page( 'elements_hive', 'Home', 'Home', 'manage_options', 'elements_hive', __NAMESPACE__ . '\Pages\Home\render' );

	add_submenu_page( 'elements_hive', 'Cloudflare Turnstile', 'Cloudflare Turnstile', 'manage_options', 'elements_hive_cloudflare_turnstile', __NAMESPACE__ . '\Pages\CloudflareTurnstile\render' );
}
